    pub fn from_properties(instance_version: Version, properties: []const ExtensionProperties) DeviceExtensions {
        var self: DeviceExtensions = .{
            .core_version = instance_version,
        };
        for (properties) |*prop| {
            self.enable_by_name(&prop.extension_name);
        }
        return self;
    }

