    pub fn enumerate_instance_extension_properties_to_array(
        self: GlobalCommands,
        allocator: Allocator,
        p_layer_name: ?[*:0]const u8,
    ) EnumerateInstanceExtensionPropertiesOrAllocatorError![]ExtensionProperties {
        const enumerator = struct {
            self: *const GlobalCommands,
            p_layer_name: ?[*:0]const u8,
